"use strict";

module.exports = function(args, start, end) {
	args = Array.prototype.slice.call(args, start, end);

	args.get = function() {
		if (this[0] == null) {
			return this.shift();
		} else {
			for (var i = 0, max = arguments.length; i < max; ++i) {
				if (arguments[i](this[0])) {
					return this.shift();
				}
			}
			return undefined;
		}
	};

	return args;
};